<?php


$query_host_services	=	'SELECT 									'.$ndoDB_assoc["db_prefix"].'hosts.host_id,									'.$ndoDB_assoc["db_prefix"].'hosts.alias,									'.$ndoDB_assoc["db_prefix"].'hosts.display_name AS hostname,									'.$ndoDB_assoc["db_prefix"].'hosts.config_type,									'.$ndoDB_assoc["db_prefix"].'services.service_id,									'.$ndoDB_assoc["db_prefix"].'services.config_type,									'.$ndoDB_assoc["db_prefix"].'services.display_name,									'.$ndoDB_assoc["db_prefix"].'services.icon_image,									'.$ndoDB_assoc["db_prefix"].'servicestatus.servicestatus_id,									'.$ndoDB_assoc["db_prefix"].'servicestatus.output,									'.$ndoDB_assoc["db_prefix"].'servicestatus.current_state,									'.$ndoDB_assoc["db_prefix"].'servicestatus.last_state_change									FROM									('.$ndoDB_assoc["db_name"].'.'.$ndoDB_assoc["db_prefix"].'services '.$ndoDB_assoc["db_prefix"].'services									INNER JOIN									'.$ndoDB_assoc["db_name"].'.'.$ndoDB_assoc["db_prefix"].'hosts '.$ndoDB_assoc["db_prefix"].'hosts									ON ('.$ndoDB_assoc["db_prefix"].'services.host_object_id = '.$ndoDB_assoc["db_prefix"].'hosts.host_object_id))									INNER JOIN									'.$ndoDB_assoc["db_name"].'.'.$ndoDB_assoc["db_prefix"].'servicestatus '.$ndoDB_assoc["db_prefix"].'servicestatus									ON ('.$ndoDB_assoc["db_prefix"].'servicestatus.service_object_id =									'.$ndoDB_assoc["db_prefix"].'services.service_object_id)									WHERE (('.$ndoDB_assoc["db_prefix"].'hosts.config_type = 0)									AND('.$ndoDB_assoc["db_prefix"].'services.config_type = 0))
									AND('.$ndoDB_assoc["db_prefix"].'services.host_object_id = (SELECT object_id FROM '.$ndoDB_assoc["db_name"].'.'.$ndoDB_assoc["db_prefix"].'objects WHERE name1 = (SELECT '.$ndoDB_assoc["db_prefix"].'hosts.display_name FROM '.$ndoDB_assoc["db_name"].'.'.$ndoDB_assoc["db_prefix"].'hosts WHERE host_id = "'.$host_id.'") AND (objecttype_id = "1")))';

?>
